(56, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-02-28 17:21:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-02-28 17:21:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2024-03-02 17:21:36', NULL, 0, 'plain', '', 0, 1, 'info'),
(59, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-02-28 17:21:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-02-28 17:21:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-02-28 17:21:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'amazon_pay_reactivate_q1-24', 'marketing', 'en_US', 'Special offer: Activate 0% promo rate for Amazon Pay', 'For a limited time, Woo merchants who register with Amazon Pay can enjoy a promotional rate of 0% on all transaction fees for 30 days. Valid for businesses based in the United Kingdom. Terms and conditions apply.', '{}', 'pending', 'woocommerce.com', '2024-02-28 17:21:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-02-28 17:21:25', NULL, 0, 'plain', '', 0, 0, 'info'),
(64, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to Woo.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2024-02-28 17:21:25', NULL, 0, 'plain', '', 0, 1, 'info'),
(65, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2024-02-29 17:22:14', NULL, 0, 'plain', '', 0, 1, 'info'),
(66, 'wc_admin_UKpricingchange_WCPay_Q1_2024', 'marketing', 'en_US', 'Fee Changes for WooPayments in the EEA and UK', 'WooPayments fees for transactions between the EEA and UK will change as of 1st March, 2024. Transactions between the UK and EEA are now deemed by card networks to be cross-border, thus international payment fees will be applicable. This also changes base processing fees.', '{}', 'pending', 'woocommerce.com', '2024-02-29 17:22:16', NULL, 0, 'plain', '', 0, 0, 'info'),
(67, 'airwallex_q1_2024', 'marketing', 'en_US', 'Save big on foreign exchange (FX) fees', 'Boost your international sales by offering Apple Pay, Google Pay, and 60+ local payment methods with Airwallex. Save time and money by settling payments directly in USD, EUR, HKD, SGD, AUD, and more — without the extra FX fees. Simple, cost-effective, fast.', '{}', 'unactioned', 'woocommerce.com', '2024-03-02 11:49:26', NULL, 0, 'plain', '', 0, 1, 'info'),
(68, 'wc-admin-add-first-product-note', 'email', 'en_US', 'Add your first product', '{greetings}<br /><br />Nice one; you\'ve created a WooCommerce store! Now it\'s time to add your first product and get ready to start selling.<br /><br />There are three ways to add your products: you can <strong>create products manually, import them at once via CSV file</strong>, or <strong>migrate them from another service</strong>.<br /><br /><a href=\"https://woo.com/document/managing-products/?utm_source=help_panel&amp;utm_medium=product\">Explore our docs</a> for more information, or just get started!', '{\"role\":\"administrator\"}', 'unactioned', 'woocommerce-admin', '2024-03-01 17:21:50', NULL, 0, 'plain', 'http://sallysweets-wp.local/wp-content/plugins/woocommerce/images/admin_notes/dashboard-widget-setup.png', 0, 0, 'info'),
(69, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics – wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2024-03-01 17:21:50', NULL, 0, 'plain', '', 0, 1, 'info'),
(70, 'wc-payments-notes-set-https-for-checkout', 'info', 'en_US', 'Enable secure checkout', 'Enable HTTPS on your checkout pages to display all available payment methods and protect your customers data.', '{}', 'unactioned', 'woocommerce-payments', '2024-03-02 11:49:29', NULL, 0, 'plain', '', 0, 1, 'info'),
(71, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2024-03-02 17:21:34', NULL, 0, 'plain', '', 0, 0, 'info'),
(72, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customise your product catalogue', 'You want your product catalogue and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2024-03-03 20:33:57', NULL, 0, 'plain', '', 0, 1, 'info'),
(73, 'wc-admin-new-sales-record', 'info', 'en_US', 'New sales record!', 'Woohoo, March 4th was your record day for sales! Net sales was £20.53 beating the previous record of £12.50 set on March 3rd.', '{\"old_record_date\":\"2024-03-03\",\"old_record_amt\":12.5,\"new_record_date\":\"2024-03-04\",\"new_record_amt\":20.53}', 'unactioned', 'woocommerce-admin', '2024-03-05 19:24:46', NULL, 0, 'plain', '', 0, 1, 'info');
